INSERT INTO allergens (name, image, info) VALUES
    ('Fish', 'static/fish.png', '{"What is Fish Allergy?":"A fish allergy occurs when the immune system mistakenly identifies proteins in fish as harmful, triggering an allergic reaction.","Allergic Reactions":"Symptoms can range from mild hives and swelling to severe anaphylaxis."}'),
    ('Milk', 'static/milk.png', '{"What is Milk Allergy?":"Milk allergy is an immune response to proteins found in cows milk, such as casein and whey.","Allergic Reactions":"Symptoms include skin rashes, vomiting, wheezing, and anaphylaxis."}'),
    ('Eggs', 'static/eggs.png', '{"What is Egg Allergy?":"Egg allergy occurs when the immune system reacts to proteins found in egg whites or yolks.","Allergic Reactions":"Symptoms may include skin hives, digestive issues, respiratory problems, and anaphylaxis."}'),
    ('Peanut', 'static/peanut.png', '{"What is Peanut Allergy?":"Peanut allergy is a common and potentially severe allergy caused by an immune system overreaction to peanut proteins.","Allergic Reactions":"Reactions range from mild itching and swelling to severe anaphylaxis."}'),
    ('Soy', 'static/soy.png', '{"What is Soy Allergy?":"Soy allergy is an immune system reaction to proteins found in soybeans.","Allergic Reactions":"Symptoms include hives, stomach pain, respiratory distress, and anaphylaxis."}'),
    ('Wheat', 'static/wheat.png', '{"What is Wheat Allergy?":"Wheat allergy is a reaction to proteins found in wheat, including gluten.","Allergic Reactions":"Reactions range from hives and nausea to severe anaphylaxis."}'),
    ('Tree Nuts', 'static/treenut.png', '{"What is Tree Nut Allergy?":"Tree nut allergy involves an immune reaction to nuts like almonds, walnuts, cashews, and pistachios.","Allergic Reactions":"Symptoms include swelling, difficulty breathing, and anaphylaxis."}'),
    ('Shellfish', 'static/shellfish.png', '{"What is Shellfish Allergy?":"Shellfish allergy is a reaction to proteins in crustaceans and mollusks.","Allergic Reactions":"Symptoms include skin rashes, gastrointestinal distress, and anaphylaxis."}'),
    ('Sesame', 'static/sesame.png', '{"What is Sesame Allergy?":"Sesame allergy is an immune reaction to proteins in sesame seeds.","Allergic Reactions":"Symptoms range from mild itching to life-threatening anaphylaxis."}'),
    ('Corn', 'static/corn.png', '{"What is Corn Allergy?":"Corn allergy is an adverse immune response to proteins found in corn and its derivatives.","Allergic Reactions":"Symptoms may include skin irritation, respiratory issues, and anaphylaxis."}'),
    ('Meat', 'static/meat.png', '{"What is Meat Allergy?":"Meat allergy occurs when the immune system reacts to proteins in beef, pork, lamb, or poultry.","Allergic Reactions":"Symptoms include stomach pain, hives, and in severe cases, anaphylaxis."}'),
    ('Gelatin', 'static/gelatin.png', '{"What is Gelatin Allergy?":"Gelatin allergy is a reaction to proteins in animal-derived gelatin.","Allergic Reactions":"Reactions range from mild hives to anaphylaxis."}'),
    ('Seeds', 'static/seeds.png', '{"What is Seed Allergy?":"Seed allergies include reactions to seeds like sunflower, flax, and mustard.","Allergic Reactions":"Symptoms vary from mild rashes to severe anaphylaxis."}'),
    ('Spices', 'static/spice.png', '{"What is Spice Allergy?":"Spice allergies involve reactions to seasonings like cinnamon, garlic, and paprika.","Allergic Reactions":"Symptoms include skin rashes, digestive upset, and respiratory issues."}'),
    ('Fruits', 'static/fruits.png','{"What is Fruit Allergy?":"Fruit allergies result from immune reactions to proteins in various fruits.","Allergic Reactions":"Symptoms range from oral allergy syndrome to severe anaphylaxis."}'),
    ('Tree Pollen', 'static/treepollen.png', '{"What is Tree Pollen Allergy?":"Tree pollen allergy is a seasonal allergic reaction to pollen from trees.","Allergic Reactions":"Symptoms include sneezing, itchy eyes, and asthma-like responses."}');
